AJS.test.require(["jira.webresources:jira-metadata"],function(){require(["jira/util/strings"],function(a){module("Strings"),test("Test for nulls in string",function(){equal(a.startsWith(null,"str"),!1),equal(a.endsWith(null,"str"),!1),equal(a.substringAfter(null,"str"),null),equal(a.contains(null,"str"),!1)}),test("Test for nulls in a search string",function(){equal(a.startsWith("str",null),!1),equal(a.endsWith("str",null),!1),equal(a.substringAfter("str",null),""),equal(a.contains("str",null),!1)}),test("Empty string should have an empty hash",function(){equal(a.hashCode(),"","Empty string returns empty hash"),equal(a.hashCode(null),"","Empty string returns empty hash"),equal(a.hashCode(""),"","Empty string returns empty hash")}),test("Returns hash of a string",function(){equal(a.hashCode("test"),"3556498","String gets hashed")}),test("Replace string",function(){equal(a.replace("test","e","o"),"tost","Single character should be replaced"),equal(a.replace("test","test","beer"),"beer","Full string match should be replaced"),equal(a.replace("my best test for a string replace test","test","quest"),"my best quest for a string replace quest","All of the multiple string matches should be replaced"),equal(a.replace("abba abba abba abba","abba","abba abba"),"abba abba abba abba abba abba abba abba","String should be replaced even if replace string is the same"),equal(a.replace("|abba| |abba| |abba| |abba|","abba",""),"|| || || ||","Replace string empty should remove occurrence from the original string"),equal(a.replace("|abba| |abba| |abba| |abba|","not matched",""),"|abba| |abba| |abba| |abba|","No match should not be replaced")})})});